import React, { useState } from "react";
import { jsPDF } from "jspdf";
import Logo from "../Assets/Logo4.png";

    const generateReceipt = (donorName, email, mobileNumber,donationAmount) => {
      const doc = new jsPDF();

      // Add Stylish Header
      doc.setFillColor("#E8EEFC");
      doc.rect(0, 0, 210, 30, "F");

      // Add Organization Logo
const logoUrl =
        "data:image/png;base64,"; // Replace with your logo URL
      doc.addImage(logoUrl, "PNG", 15, 5, 20, 20);

      // Style for Text
      doc.setFont("helvetica", "bold");
      doc.setFontSize(20);
      doc.setTextColor("#1e88e5");

      // Add Organization Name
      doc.text("Sankalp Sahayata Organization", 50, 20);
      
      // Style for Subheader
      doc.setFontSize(12);
      doc.setTextColor("#000000");

      // Add Receipt Details
      doc.text(
        `Receipt Number: ${Math.floor(Math.random() * 1000000) + 100000}`,
        20,
        40
      );
      doc.text(
        `Receipt Issued Date: ${new Date().toLocaleDateString()}`,
        20,
        50
      );
      doc.text("Location: India", 20, 60);
      doc.text(`Donated By: ${donorName}`, 20, 70);
      doc.text(`Email: ${email}`, 20, 80);
      doc.text(`Mobile Number: ${mobileNumber}`, 20, 90);
      doc.text(`Date of Donation: ${new Date().toLocaleDateString()}`, 20, 100);

      // Highlight Donation Amount
      doc.setTextColor("#f44336"); // Red color for highlight
      doc.setFont("helvetica", "bold");
      doc.setFontSize(20);
      doc.text(`Amount Donated: Rs. ${donationAmount}`, 20, 120);

      // Reset Text Style
      doc.setTextColor("#000000");
      doc.setFont("helvetica", "normal");
      doc.setFontSize(12);

      // Add Authorized Signature
      doc.text("Authorized Signature:", 20, 134);
doc.addImage(
  "data:image/webp;base64,",
  "WEBP",
  20,
  140,
  30,
  10
);
      // Add Vision and Mission
      doc.setFont("helvetica", "italic");
      doc.setFontSize(14);
      // Add Vision
      const visionText =
        "ur vision is a world where every individual thrives, irrespective of circumstance. We aspire to be a beacon of hope, fostering empathy, inclusivity, and collective responsibility for a brighter, more compassionate global community.";
      const visionLines = doc.splitTextToSize(visionText, 170); // Adjust the width as needed
      doc.text("Vision:", 20, 160);
      doc.text(visionLines, 20, 170);

      // Add Mission
      const missionText =
        "Our mission is to uplift communities through strategic initiatives in education, healthcare, and support for the underserved. With transparency, community engagement, and a commitment to sustainable change, we strive to make a meaningful impact. Join us in this compassionate journey.";
      const missionLines = doc.splitTextToSize(missionText, 170); // Adjust the width as needed
      doc.text("Mission:", 20, 200);
      doc.text(missionLines, 20, 210);

      // Thank You Message
      doc.setFont("helvetica", "bold");
      doc.setFontSize(16);
      doc.text("Thank You for Your Generosity!", 20, 250);

      // Save the PDF
      doc.save("donation_receipt.pdf");
      // Save the PDF content as a data URL
      // const pdfDataUri = doc.output("dataurl");

      // // Open the PDF in a new tab
      // const newTab = window.open();
      // newTab.document.write(
      //   `<iframe width='100%' height='100%' src='${pdfDataUri}'></iframe>`
      // );
    };

 

export default generateReceipt;
